import { useState, useEffect } from "react";
import WorkoutSelector from "../components/WorkoutSelector";
import WorkoutList from "../components/WorkoutList";
import WorkoutHistory from "../components/WorkoutHistory";
import ProgressChart from "../components/ProgressChart";
import { saveProgress, loadProgress } from "../utils/localStorageHelper";
import { motion } from "framer-motion";

const Dashboard = ({ onLogout }) => {
  const [selectedWorkout, setSelectedWorkout] = useState("");
  const [workoutData, setWorkoutData] = useState({});
  const [history, setHistory] = useState([]);
  const [showProgress, setShowProgress] = useState(false);

  // ‚úÖ Load saved progress on mount & when workout selection changes
  useEffect(() => {
    const savedData = loadProgress() || {};
    console.log("üìÇ Loaded Saved Data from LocalStorage:", savedData);

    if (selectedWorkout) {
      const workoutEntries = savedData[selectedWorkout] || {};
      console.log("üîÑ Setting Workout Data for:", selectedWorkout, workoutEntries);

      setWorkoutData((prevData) => ({
        ...prevData,
        ...workoutEntries, // ‚úÖ Merge previous & saved data
      }));
    }

    setHistory(savedData.history || []);
  }, [selectedWorkout]);

  // ‚úÖ Update Exercise Data & Prevent Unnecessary Overrides
  const updateExerciseData = (exercise, field, value) => {
    setWorkoutData((prev) => {
      const updatedData = {
        ...prev,
        [exercise]: {
          ...prev[exercise], // ‚úÖ Preserve other fields
          weight1: prev[exercise]?.weight1 || "",
          weight2: prev[exercise]?.weight2 || "",
          weight3: prev[exercise]?.weight3 || "",
          [field]: value.replace(/^0+/, ""), // ‚úÖ Remove leading zeroes
        },
      };

      console.log(`‚úèÔ∏è Updated Workout Data for ${exercise} - ${field}:`, updatedData);
      return updatedData;
    });
  };

  // ‚úÖ Save Workout & Ensure Data is Stored Correctly
  const saveWorkout = () => {
    const date = new Date().toLocaleDateString();
    console.log("üíæ Saving Workout for Date:", date);

    // ‚úÖ Clone state before modifying to prevent unexpected changes
    const cleanedExercises = JSON.parse(JSON.stringify(workoutData));

    console.log("üìä Final Workout Data Before Saving:", cleanedExercises);

    const updatedHistory = [
      { date, workout: selectedWorkout, exercises: cleanedExercises },
      ...history,
    ];

    const allProgress = loadProgress() || {};
    allProgress.history = updatedHistory;
    allProgress[selectedWorkout] = cleanedExercises; // ‚úÖ Save by category
    saveProgress(allProgress);

    setHistory(updatedHistory);
    console.log("‚úÖ Final History Saved to LocalStorage:", updatedHistory);
    alert("Workout Saved! üéâ");
  };

  // ‚úÖ Reset all workout history and progress
  const resetProgress = () => {
    if (window.confirm("Are you sure you want to reset all progress? This cannot be undone.")) {
      localStorage.clear();
      setHistory([]);
      setWorkoutData({});
      console.log("üö® All Progress Reset!");
      alert("All progress has been reset! üóëÔ∏è");
    }
  };

  return (
    <motion.div className="dashboard">
      <div className="dashboard-header">
        <h1>üèãÔ∏è‚Äç‚ôÇÔ∏è GYM WORKOUT TRACKER</h1>
        <button className="logout-button" onClick={onLogout}>üö™ Logout</button>
      </div>

      <WorkoutSelector selectedWorkout={selectedWorkout} setSelectedWorkout={setSelectedWorkout} />

      <WorkoutList
        selectedWorkout={selectedWorkout}
        workoutData={workoutData}
        updateExerciseData={updateExerciseData}
        saveWorkout={saveWorkout}
      />

      <WorkoutHistory history={history} />

      <button className="progress-toggle" onClick={() => setShowProgress(!showProgress)}>
        {showProgress ? "Hide Progress üìâ" : "Show Progress üìà"}
      </button>

      {showProgress && <ProgressChart />}

      <button className="reset-button" onClick={resetProgress}>Reset Progress üóëÔ∏è</button>
    </motion.div>
  );
};

export default Dashboard;
