import { useState, useEffect } from "react";
import WorkoutSelector from "../components/WorkoutSelector";
import WorkoutList from "../components/WorkoutList";
import WorkoutHistory from "../components/WorkoutHistory";
import ProgressChart from "../components/ProgressChart";
import { saveProgress, loadProgress } from "../utils/localStorageHelper";
import { motion } from "framer-motion";

const Dashboard = ({ onLogout }) => {
  const [selectedWorkout, setSelectedWorkout] = useState("");
  const [workoutData, setWorkoutData] = useState({});
  const [history, setHistory] = useState([]);
  const [showProgress, setShowProgress] = useState(false);

  useEffect(() => {
    const savedData = loadProgress();
    if (selectedWorkout) {
      setWorkoutData(savedData[selectedWorkout] || {});
    }
    setHistory(savedData.history || []);
  }, [selectedWorkout]);

  const updateExerciseData = (exercise, field, value) => {
    setWorkoutData((prev) => ({
      ...prev,
      [exercise]: {
        ...prev[exercise],
        [field]: value !== "" ? String(value) : "0",
      },
    }));
  };

  // ✅ Save Workout to Google Sheets & Local History
  const saveWorkout = async () => {
    const date = new Date().toLocaleDateString();

    const cleanedExercises = {};
    const promises = Object.keys(workoutData).map(async (exercise) => {
      const workoutEntry = {
        date,
        category: selectedWorkout,
        exercise,
        weight1: workoutData[exercise]?.weight1 || "0",
        weight2: workoutData[exercise]?.weight2 || "0",
        weight3: workoutData[exercise]?.weight3 || "0",
        completed: workoutData[exercise]?.completed ? "✅" : "❌",
      };

      // ✅ Send workout data to Google Sheets
      try {
        await fetch("Yhttps://script.google.com/macros/s/AKfycbw93MXgdbfIDpW-sNa1jlvHbvqqkBn9cBZMlJG5HGgkuPIcN7Cm73K5KTLad94EY-B7Wg/exec", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(workoutEntry),
        });
        console.log("Workout logged:", workoutEntry);
      } catch (error) {
        console.error("Error saving to Google Sheets:", error);
      }

      cleanedExercises[exercise] = workoutEntry;
    });

    await Promise.all(promises); // ✅ Wait for all fetch requests to complete

    // ✅ Save Data to Local History
    const updatedHistory = [
      { date, workout: selectedWorkout, exercises: cleanedExercises },
      ...history,
    ];

    const allProgress = loadProgress();
    allProgress.history = updatedHistory;
    saveProgress(allProgress);

    setHistory(updatedHistory);
    alert("Workout Saved & Synced to Google Sheets! 🎉");
  };

  // 🗑️ Reset all workout history and progress
  const resetProgress = () => {
    if (window.confirm("Are you sure you want to reset all progress? This cannot be undone.")) {
      localStorage.clear(); // Clear all stored progress
      setHistory([]); // Reset history state
      setWorkoutData({}); // Reset workout data state
      alert("All progress has been reset! 🗑️");
    }
  };

  return (
    <motion.div className="dashboard">
      <div className="dashboard-header">
        <h1>🏋️‍♂️ GYM WORKOUT TRACKER</h1>
        <button className="logout-button" onClick={onLogout}>🚪 Logout</button>
      </div>

      <WorkoutSelector selectedWorkout={selectedWorkout} setSelectedWorkout={setSelectedWorkout} />

      <WorkoutList
        selectedWorkout={selectedWorkout}
        workoutData={workoutData}
        updateExerciseData={updateExerciseData}
        saveWorkout={saveWorkout}
      />

      <WorkoutHistory history={history} />

      {/* 📊 Button to Show/Hide Progress Chart */}
      <button className="progress-toggle" onClick={() => setShowProgress(!showProgress)}>
        {showProgress ? "Hide Progress 📉" : "Show Progress 📈"}
      </button>

      {/* 📈 Show Progress Chart Only When Button is Clicked */}
      {showProgress && <ProgressChart />}

      {/* 🗑️ Reset Button */}
      <button className="reset-button" onClick={resetProgress}>Reset Progress 🗑️</button>
    </motion.div>
  );
};

export default Dashboard;
